import React from 'react'
import classNames from 'classnames'

import useFetch from 'hooks/useFetch'

const AddToFavorites = ({isFavorited, favoritesCount, articleSlug}) => {
  const apiUrl = `/articles/${articleSlug}/favorite`
  const [{ response }, doFetch] = useFetch(apiUrl)
  const favotitesCountWithResponse = response
    ? response.article.favoritesCount
    : favoritesCount
  const isFavotitedWithResponse = response
    ? response.article.favorited
    : isFavorited
  const buttonClasses = classNames({
    btn: 'true',
    'btn-active': isFavotitedWithResponse,
    'btn-outline-active': !isFavotitedWithResponse
  })

  const handleLick = event => {
    doFetch({
      method: isFavotitedWithResponse ? 'delete' : 'post'
    })
  }
  return (
    <button
      className={buttonClasses}
      onClick={handleLick}
    >
      <i className="ion-heart"></i>
      <span>&nbsp; {favotitesCountWithResponse}</span>
    </button>
  )
}

export default AddToFavorites
