import React, {useState, useEffect} from 'react'
import {Link, Redirect} from 'react-router-dom'

import useFetch from 'hooks/useFetch'
import useLocalStorage from 'hooks/useLocalStorage'

const Authentication = props => {
  const isLogin = props.match.path === '/login'

  const pageTitle = isLogin ? 'Войти': 'Регистрация'
  const descrLink = isLogin ? '/register': '/login'
  const descrText = isLogin ? 'Зарегистрироваться?': 'Войти?'
  const apiUrl = isLogin ? '/users/login': '/users'

  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [username, setUsername] = useState('')
  const [isSuccessfullSubmit, setIsSuccessfullSubmit] = useState(false)
  const [token, setToken] = useLocalStorage('token')
  const [{response, isLoading, error}, doFetch] = useFetch(apiUrl)

  const handleSubmit = event => {
    event.preventDefault()
    const user = isLogin ? {email, password} : {email, password, username}
    doFetch({
      data: {
        user
      }
    })
  }

  useEffect(() => {
    if (!response) {
      return
    }
    console.log(response)
    setToken(response.user.token)
    setIsSuccessfullSubmit(true)
  }, [response])

  if (isSuccessfullSubmit) {
    return <Redirect to="/" />
  }

  return (
    <div className="auth-page">
      <div className="container">
        <div className="row">
          <div className="form-wrapper">
            <h1>{pageTitle}</h1>
            <p>
              <Link to={descrLink}>{descrText}</Link>
            </p>
            <form onSubmit={handleSubmit}>
              {!isLogin && (
                <p className="wrp-field">
                  <input
                    type="text"
                    placeholder="Имя"
                    value={username}
                    onChange={e => setUsername(e.target.value)}
                  />
                </p>
              )}
              <p className="wrp-field">
                <input
                  type="email"
                  placeholder="Email"
                  value={email}
                  onChange={e => setEmail(e.target.value)}
                />
              </p>
              <p className="wrp-field">
                <input
                  type="password"
                  placeholder="Пароль"
                  value={password}
                  onChange={e => setPassword(e.target.value)}
                />
              </p>
              <button
                type="submit"
                className="btn btn-blue"
                disabled={isLoading}
              >
                {isLoading && <span className="btn-loader">&nbsp;</span>}
                {pageTitle}
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Authentication
