import React, {useEffect} from 'react'
import {NavLink} from 'react-router-dom'

import useFetch from 'hooks/useFetch'
import UserArticles from 'pages/userProfile/components/userArticles'

const UserProfile = ({location, match}) => {
  const slug = match.params.slug
  const isFavorites = location.pathname.includes('favorites')
  const apiUrl = `/profiles/${slug}`
  const [{ response }, doFetch] = useFetch(apiUrl)

  useEffect(()=> {
    doFetch()
  }, [doFetch])

  if (!response) {
    return null
  }


  return (
    <div className="profile-page">
      <div className="user-info">
        <div className="container">
          <div className="row">
            <img src={response.profile.image} alt="" />
            <h4>{response.profile.username}</h4>
            <p>{response.profile.bio}</p>
          </div>
        </div>
      </div>
      <div className="container">
        <div className="row">
          <div className="articles-toggle">
            <ul>
              <li>
                <NavLink to={`/profiles/${response.profile.username}`} exact>
                  Мои посты
                </NavLink>
              </li>
              <li>
                <NavLink to={`/profiles/${response.profile.username}/favorites`}>
                  Избранные посты
                </NavLink>
              </li>
            </ul>
          </div>
          <UserArticles
            username={response.profile.username}
            location={location}
            isFavorites={isFavorites}
            url={match.url}
          />
        </div>
      </div>
    </div>
  )
}

export default UserProfile
